from bs4 import BeautifulSoup

# BeautifulSoup을 사용하여 HTML 파싱
soup = BeautifulSoup(text, "html.parser")

# 모든 카테고리 아이템을 가져오기
category_items = soup.find_all("li", class_="_searchCategoryItem_search_category_item_dsx5f")

# 상품 번호
n = 0

# 상품리스트
categories = []

# 결과 출력
for category_item in category_items:
    # 대분류 카테고리 이름 추출
    category_name = category_item.find('strong', class_='_searchCategoryItem_text_2SP8Q').text.strip()
    print(f"대분류 카테고리: {category_name}")
    categories.append(category_name)
    
    # 소분류 카테고리 추출
    sub_categories = category_item.find_all("li", class_="_searchCategoryItem_sub_item_Ivd-P")
    for sub_category in sub_categories:
        sub_category_name = sub_category.text.strip()
        n += 1
        print(f"{n} {sub_category_name}")
        categories.append(sub_category_name)

# '/' 기준 단어 분할
new_categories = []

for category in categories:
    if "/" in category:
        new_categories.extend(category.split("/"))
    else:
        new_categories.append(category)

print(new_categories)

# 중복 제거된 리스트
unique_categories = list(set(new_categories))

for index, category in enumerate(unique_categories, start=1):
    print(f"{index} {category}")